  computed: {
    groundW: function () {
      return this.loadedImages['ground'].width;
    },
    groundH: function () {
      return this.loadedImages['ground'].height;
    },
    dinoW: function () {
      return this.loadedImages['initial'].width;
    },
    dinoH: function () {
      return this.loadedImages['initial'].height;
    },
    groundY: function () {
      return this.canvasHeight - this.loadedImages['ground'].height - this.dinoH;
    }
  },
  methods: {
    // 绘制图片
    drawPic(pic, x, y, w, h) {
      let img = this.loadedImages[pic]; // 使用已加载的图片

      if (!img) return; // 如果图片尚未加载，直接返回

      if (w === undefined) w = img.width;
      if (h === undefined) h = img.height;
      if (y === undefined) y = this.groundY;
      if (x === undefined) x = 0;
      this.ctx.drawImage(img, x, y, w, h);
    },
    // 更新地面位置
    updateGroundPosition() {
      this.groundX -= this.speed;
      if (this.groundX <= -this.canvasWidth) {
        this.groundX = 0;
      }
    },
    // 处理键盘函数
    handleKeyDown(e) {
      if (e.key === 'Enter' && !this.isRunning) {
        this.isRunning = true;
        this.speed = 1;
        this.play(); // 启动动画
      }
      if (this.isRunning && e.key === ' ' && !this.isJumping) {
        // 按下空格键
        this.isJumping = true; // 标记为正在跳跃
        this.jumpHeight = 0; // 重置跳跃高度
        this.jumpState = this.JUMP_STATE_ASCENDING;
        this.jumpStartTime = Date.now(); // 记录跳跃开始时间
        this.lastUpdateTime = Date.now(); // 初始化最后更新时间
      }
    },
    // 加速函数
    accelerate() {
      this.speed += 0.001;
      this.pace -= 1;
    },
    // 跳跃
    // updateJump() {
    //   if (this.isJumping) {
    //     // 上升阶段
    //     if (this.jumpHeight < 70) {
    //       this.jumpHeight += this.gravity // 增加跳跃高度
    //     } else {
    //       // 达到最高点，开始滞空
    //       this.isJumping = false // 结束跳跃
    //       this.isHanging = true // 设置为滞空状态
    //       this.jumpHeight = 70 // 确保高度在最高点

    //       // 设置滞空定时器
    //       setTimeout(() => {
    //         this.isHanging = false // 结束滞空
    //         this.gravity = -0.5 // 开始下降
    //       }, 30000) // 0.3秒的滞空时间
    //     }
    //   } else if (this.isHanging) {
